/**
 * @module ai/aireviewmode/aireviewcheckdefinitions
 */
/**
 * List of definitions of available review checks that can be performed in the AI Review Mode.
 */
export declare const AI_REVIEW_CHECKS: Array<AIReviewCheckDefinition>;
export type AIReviewCheckOptionData = {
    label: string;
    id: string;
};
export type AIReviewCheckOptions = {
    formType: string;
    label?: string;
    default?: string;
    data?: Array<AIReviewCheckOptionData>;
    argsKey?: string;
};
export type AIReviewCheckDefinition = {
    id: string;
    title: string;
    description: string;
    isParameterized: boolean;
    useArgs?: boolean;
    options?: Array<AIReviewCheckOptions>;
};
